import React from 'react'
import "./notifications.css"
import Notification from './notifaction/Notification'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faXmark } from '@fortawesome/free-solid-svg-icons'
import $ from "jquery"
export default function Notifications({notifState}) {

    if(notifState){
        $(".notifications").css("transform","translateY(0)")
    }else{
        $(".notifications").css("transform","translateY(-200%)")
    }

    function handleClick(){
         let NewnotifState = false

         if(!NewnotifState){
            $(".notifications").css("transform","translateY(-200%)")
         }else{
            $(".notifications").css("transform","translateY(0)")
         }
    }

  return (
    <div className="notifications">
        <FontAwesomeIcon icon={faXmark} onClick={handleClick} />
        <h1>Notifications</h1>
        <div className="list_notifications">
            <Notification/>
            <Notification/>
            <Notification/>
            <Notification/>
            <Notification/>
        </div>
  </div>
  )
}
