import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./signIn.css";
import logo from "../logo/IMG-20220426-WA0014-removebg-preview.png";

import img from "./undraw_community_re_cyrm.svg";
import axios from "axios";
function SignIn() {
  const [registerForm, setRegisterForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmePassword: "",
  });

  function handleChange(e) {
    const { name, value } = e.target;
    setRegisterForm((prevValue) => {
      switch (name) {
        case "firstName":
          return {
            firstName: value,
            lastName: prevValue.email,
            email: prevValue.email,
            password: prevValue.password,
            confirmePassword: prevValue.confirmePassword,
          };

        case "lastName":
          return {
            firstName: prevValue.firstName,
            lastName: value,
            email: prevValue.email,
            password: prevValue.password,
            confirmePassword: prevValue.confirmePassword,
          };

        case "email":
          return {
            firstName: prevValue.firstName,
            lastName: prevValue.email,
            email: value,
            password: prevValue.password,
            confirmePassword: prevValue.confirmePassword,
          };

        case "password":
          return {
            firstName: prevValue.firstName,
            lastName: prevValue.lastName,
            email: prevValue.email,
            password: value,
            confirmePassword: prevValue.confirmePassword,
          };

        case "confirmePassword":
          return {
            firstName: prevValue.firstName,
            lastName: prevValue.lastName,
            email: prevValue.email,
            password: prevValue.password,
            confirmePassword: value,
          };

        default:
          break;
      }
    });
  }

  function handleVerif() {
    if (
      registerForm.firstName.length === 0 ||
      registerForm.email.indexOf("@") === -1 ||
      registerForm.password.length < 8 ||
      registerForm.confirmePassword.length < 8 ||
      registerForm.password !== registerForm.confirmePassword
    ) {
      return false;
    } else {
      return true;
    }
  }

  function handleSubmit(e) {
    e.preventDefault();
    const verif = handleVerif();
    if (verif) {
      axios
        .post(`https://jsonplaceholder.typicode.com/users`, registerForm)
        .then((res) => {
          console.log(res);
          console.log(res.data);
        });
    } else {
      const smalls = Array.from(
        document.querySelectorAll(
          ".main .container_signin .right_div form small"
        )
      );
      smalls.forEach((s) => {
        s.style.display = "block";
      });
    }
    console.log(registerForm);
  }

  return (
    <div className="main_signin">
      <div className="container_signin">
        <div className="left_div">
          <img className="img1" src={logo} alt="logo" />
          <h4>Welcome to our community</h4>
          <img className="omg2" src={img} alt="community" />
        </div>
        <div className="right_div">
          <h2>Register</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="firstName"
              placeholder="FirstName"
              onBlur={handleVerif}
              onChange={handleChange}
            />
            <small>your name should be more than 0 characters</small>
            <input
              type="text"
              name="lastName"
              placeholder="lastName"
              onBlur={handleVerif}
              onChange={handleChange}
            />
            <small>your name should be more than 0 characters</small>
            <input
              type="email"
              name="email"
              placeholder="Email"
              onBlur={handleVerif}
              onChange={handleChange}
            />
            <small>email should conatin "@" , "." !</small>
            <input
              type="password"
              name="password"
              placeholder="Password"
              onBlur={handleVerif}
              onChange={handleChange}
            />
            <small>password should be more than 8 characters</small>
            <input
              type="password"
              name="confirmePassword"
              placeholder="Confirme Password"
              onBlur={handleVerif}
              onChange={handleChange}
            />
            <small>confirme password should be identical</small>
            <input className="registerButton" type="submit" value="Register" />
            <Link to="/login">Have an account ? login</Link>
          </form>
        </div>
      </div>
    </div>
  );
}

export default SignIn;
