import React, { useState } from "react";
import "./homePage.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faFilter,
  faNewspaper,
  faQuoteRightAlt,
  faXmark,
} from "@fortawesome/free-solid-svg-icons";
import $ from "jquery";
import fth_add from "./javascriptfull.png";
import sth_add from "./maxresdefault (1).jpg";
import tth_add from "./maxresdefault.jpg";
import { Link } from "react-router-dom";
import User from "./user/User";
import Add from "./add/Add";
import Article from "./article/Article";
import user_pic from "./photo-1633332755192-727a05c4013d.jpeg";
import Header from "./header/Header";
import TextEditor from "./textEditor/TextEditor";
import articles from "./article/article_backend";
function HomePage() {
  const [textEditorClick, setTextEditorClick] = useState(false);
  const [addNewClick, setAddNew] = useState(false);

  function handleTextEditor() {
    setTextEditorClick(!textEditorClick);
  }

  function handleAdd(e) {
    setAddNew(!addNewClick);
  }

  function handleChange(e) {
    console.log(e.target.value);
  }

  if (addNewClick) {
    $("#addPost").css("transform", "scale(1)");
    $("#xQuitte").css("display", "block");
  } else {
    $("#addPost").css("transform", "scale(0)");
    $("#xQuitte").css("display", "none");
  }

  if (textEditorClick) {
    $(".textEditor").css("transform", "scale(1)");
    $("#xTextEditorQuitte").css("display", "block");
  } else {
    $(".textEditor").css("transform", "scale(0)");
    $("#xTextEditorQuitte").css("display", "none");
  }

  function handlecheck(e){
    console.log(e.target.id);
  }


  return (
    <div className="homePage_container">
      <div className="main">
        <Header />
        <div className="main_conatiner">
          <div className="aside">
            <div className="aside_container">
              <div className="actions">
                <div className="filter">
                  <div className="filter_header">
                    <small>Filter</small>
                    <FontAwesomeIcon icon={faFilter} />
                  </div>
                  <div className="filter_topics">
                    <div>
                      <input type="checkbox" id="article" />
                      <label htmlFor="article">Articles</label>
                    </div>
                    <div>
                      <input type="checkbox" id="posts" />
                      <label htmlFor="posts">Posts</label>
                    </div>
                    <div>
                      <input type="checkbox" id="videos" />
                      <label htmlFor="videos">Videos</label>
                    </div>
                  </div>
                </div>
                <div className="intrests">
                  <div className="intrests_header">
                    <small>your intrestings topics</small>
                  </div>
                  <div className="intrests_topics">
                    <div>
                      <input type="checkbox" id="javascript"  />
                      <label htmlFor="article">Javascript</label>
                    </div>
                    <div>
                      <input type="checkbox" id="reactjs" />
                      <label htmlFor="posts">React Js</label>
                    </div>

                    <div>
                      <input type="checkbox" id="django"  />
                      <label htmlFor="posts">Django</label>
                    </div>

                    <div>
                      <input type="checkbox" id="css" />
                      <label htmlFor="posts">css</label>
                    </div>

                    <div>
                      <input type="checkbox" id="flask" />
                      <label htmlFor="posts">flask</label>
                    </div>
                  </div>
                </div>
              </div>

              <div className="recm">
                <small>Recommended people for you:</small>
                <div className="recm_container">
                  <User username="bill gates" />
                  <User username="steve jobs" />
                  <User username="el zero" />
                  <User username="salem cv" />
                </div>
              </div>
            </div>
          </div>
          <div className="articles_section">
            <div className="add_article">
              <Link to="/profile">
                <img src={user_pic} alt="user_picture" />
              </Link>
              <input
                type="text"
                placeholder="What subject you wanna talk about?"
                disabled
              />
              <div className="options">
                <FontAwesomeIcon
                  icon={faNewspaper}
